    }
     public function xnfrmla($tn,$pq,$sq) {
        $sm = $pq + $sq;
        $answer = floor( $tn / $sm );
        $bq = ($answer * $sm) + $pq;
        $uq = ($answer * $sm) + $sm;
        $min = min($tn,$uq);
        $dyn = $min - $bq;
        if($dyn < 0){$dyn = 0;}
        $fq = $answer * $sq;
        return $fq + $dyn;
    }

